#!/bin/bash

input_tx=""
destination_address="2MvLcssW49n9atmksjwg2ZCMsEMsoj3pzUP"
amount_satoshis=20000000
amount_btc=$(echo "scale=8; $amount_satoshis/100000000" | bc)

txid=$(bitcoin-cli -regtest decoderawtransaction "$input_tx" | jq -r '.txid')

raw_tx=$(bitcoin-cli -regtest createrawtransaction \
  '[{"txid":"'$txid'","vout":0,"sequence":1},{"txid":"'$txid'","vout":1,"sequence":1}]' \
  '{"'$destination_address'":0.20000000}')

echo "$raw_tx"

